        <!-- 添加至目标分组 -->
        <add-to-group group-id="ProjectViewPopupMenu" anchor="after" relative-to-action="RevealIn"/>
        <!-- 快捷键设置 -->
        <keyboard-shortcut keymap="$default" first-keystroke="ctrl alt T"/>
    </action>
</actions>
```


